<?phprequire('fpdf.php');require('conexion.inc');$pdf=new FPDF('P','mm',array(102,102));$pdf->SetMargins(0,0);$codigo_ingreso=$_GET['codigo_ingreso'];$sqlItems="select i.cod_material, i.nro_empaques from ingreso_detalle_almacenes i where i.cod_ingreso_almacen=$codigo_ingreso";$respItems=mysql_query($sqlItems);while($datItems=mysql_fetch_array($respItems)){		$codItem=$datItems[0];	$numeroEtiquetas=$datItems[1];		$sqlEtiquetas="select observaciones, nro_correlativo, fecha from ingreso_almacenes 	where cod_ingreso_almacen='$codigo_ingreso'";	$respEti=mysql_query($sqlEtiquetas);		//$numeroEtiquetas=mysql_result($respEti,0,0);	$obsProveedor=mysql_result($respEti,0,0);	$nroIngreso=mysql_result($respEti,0,1);	$fechaIngreso=mysql_result($respEti,0,2);	for($ii=1; $ii<=$numeroEtiquetas; $ii++){						$pdf->AddPage(); 		$pdf->Rect(7,7,90,90);		$pdf->Image('imagenes/logocofar.jpg',8,10,0);		$pdf->SetFont('Arial','B',40);		$pdf->SetXY(38,20);	$pdf->Cell(0,0,"MA $ii/$numeroEtiquetas");		$pdf->Line(7,32,97,32);						$pdf->SetFont('Arial','B',15);		$pdf->SetXY(15,35);	$pdf->Cell(0,0,"Nro. $nroIngreso    Fecha: $fechaIngreso");		$pdf->Line(7,38,97,38);					$pdf->SetFont('Arial','B',14);		$pdf->SetXY(45,41);	$pdf->Cell(0,0,"Item");		$sqlMat="select m.descripcion_material from ingreso_detalle_almacenes id, material_apoyo m 		where id.cod_material=m.codigo_material and 		id.cod_ingreso_almacen=$codigo_ingreso and id.cod_material='$codItem'";		$respMat=mysql_query($sqlMat);		$nombreMat=mysql_result($respMat,0,0);		$cad1=substr($nombreMat,0,21);		$cad2=substr($nombreMat,21,21);		$cad3=substr($nombreMat,42,21);						$pdf->SetFont('Arial','B',20);		$pdf->Line(7,44,97,44);			$pdf->SetXY(8,48);	$pdf->Cell(0,0,$cad1);		$pdf->SetXY(8,55);	$pdf->Cell(0,0,$cad2);		$pdf->SetXY(8,62);	$pdf->Cell(0,0,$cad3);						//$pdf->Line(7,59,97,59);			/*$pdf->SetFont('Arial','B',10);		$pdf->SetXY(38,62);	$pdf->Cell(0,0,"Observaciones");		$pdf->Line(7,65,97,65);			$pdf->SetXY(8,68);	$pdf->Cell(0,0,$obsProveedor);		*/		$pdf->Line(7,72,97,72);			$sqlCantidad="select cantidad from ingreso_etiquetas where cod_ingreso='$codigo_ingreso' and etiqueta='$ii' and cod_material='$codItem'";		$respCantidad=mysql_query($sqlCantidad);		$cantidadEtiqueta=mysql_result($respCantidad,0,0);				$pdf->SetXY(12,81);	$pdf->Cell(0,0,"Cantidad");		$pdf->Line(45,72,45,97);					$pdf->SetFont('Arial','B',50);		//$pdf->SetXY(55,80);	$pdf->Cell(0,0,"$ii/$numeroEtiquetas");		$pdf->SetXY(55,81);	$pdf->Cell(0,0,"$cantidadEtiqueta");	}	}$pdf->Output();			?>