<?php/** * Desarrollado por Datanet-Bolivia. * @autor: Marco Antonio Luna Gonzales * Sistema de Visita Médica * * @copyright 2005 */ 	echo "<script language='Javascript'>		function enviar_nav()		{	location.href='registrar_grupo_especial.php';		}		function editar_nav(f)		{			var i;			var j=0;			var j_linea;			for(i=0;i<=f.length-1;i++)			{				if(f.elements[i].type=='checkbox')				{	if(f.elements[i].checked==true)					{	j_linea=f.elements[i].value;						j=j+1;					}				}			}			if(j>1)			{	alert('Debe seleccionar solamente un Grupo Especial para editar sus datos.');			}			else			{				if(j==0)				{					alert('Debe seleccionar un Grupo Especial para editar sus datos.');				}				else				{					location.href='editar_grupoespe.php?cod_grupo='+j_linea+'';				}			}		}		function adi_med_nav(f)		{			var i;			var j=0;			var j_linea;			for(i=0;i<=f.length-1;i++)			{				if(f.elements[i].type=='checkbox')				{	if(f.elements[i].checked==true)					{	j_linea=f.elements[i].value;						j=j+1;					}				}			}			if(j>1)			{	alert('Debe seleccionar solamente un Grupo Especial para ver a los Medicos del grupo.');			}			else			{				if(j==0)				{					alert('Debe seleccionar un Grupo Especial para ver a los Medicos del grupo.');				}				else				{					location.href='navegador_grupoespe_detalle.php?cod_grupo='+j_linea+'';				}			}		}		function eliminar_nav(f)		{				var i;			var j=0;			datos=new Array();			for(i=0;i<=f.length-1;i++)			{				if(f.elements[i].type=='checkbox')				{	if(f.elements[i].checked==true)					{	datos[j]=f.elements[i].value;						j=j+1;					}				}			}			if(j==0)			{	alert('Debe seleccionar al menos un Grupo Especial para proceder a su eliminación.');			}			else			{					if(confirm('Esta seguro de eliminar los datos.'))				{					location.href='eliminar_grupo_especial.php?datos='+datos+'';				}				else				{					return(false);				}			}		}		</script>	";		require("conexion.inc");	require("estilos_regional_pri.inc");	echo "<form>";	$sql="select codigo_grupo_especial, nombre_grupo_especial from grupo_especial where cod_especialidad='$codigo' and codigo_linea='$global_linea' and agencia='$global_agencia' order by nombre_grupo_especial";	$resp=mysql_query($sql);	//saca la descripcion de la especialidaa	$sql_espe=mysql_query("select desc_especialidad from especialidades where cod_especialidad='$codigo'");	$dat_espe=mysql_fetch_array($sql_espe);	$especialidad=$dat_espe[0];	echo "<center><table border='0' class='textotit'><tr><td>Registro de Grupos Especiales<br>Especialidad: <strong>$especialidad</strong></td></tr></table></center><br>";	echo "<center><table border='1' class='texto' cellspacing='0'>";	echo "<tr><td>&nbsp;</td><th>Nombre Grupo Especial</th></tr>";	while($dat=mysql_fetch_array($resp))	{		$cod_grupo=$dat[0];		$nombre_grupo=$dat[1];		echo "<tr><td align='center'><input type='checkbox' name='codigo' value='$cod_grupo'></td><td align='center'>$nombre_grupo</td></tr>";	}	echo "</table></center><br>";	echo "<center><table border='0' class='texto'>";	echo "<tr><td><input type='button' value='Adicionar' name='adicionar' class='boton' onclick='enviar_nav()'></td><td><input type='button' value='Eliminar' name='eliminar' class='boton' onclick='eliminar_nav(this.form)'></td><td><input type='button' value='Editar' class='boton' onclick='editar_nav(this.form)'></td><td><input type='button' value='Medicos del Grupo' class='boton' onclick='adi_med_nav(this.form)'></td></tr></table></center>";	echo "</form>";?>